class Tripjacks_Model_News extends Tripjacks_Model_BaseNews
{
    
       public function getNews() {
        $q = Doctrine_Query::create()
                ->from('Tripjacks_Model_News n')
                ->addGroupBy('date', 'DESC');
              
        $result = $q->fetchArray();
        return $result;
    }

}